import React, { Suspense, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import { ThemeProvider, createTheme, CssBaseline } from '@mui/material';
import { Provider } from 'react-redux';
import { QueryClient, QueryClientProvider } from 'react-query';
import { Toaster } from 'react-hot-toast';
import { store } from './store/store';
import { useAuth } from './hooks/useAuth';
import LoadingSpinner from './components/LoadingSpinner';
import ErrorBoundary from './components/ErrorBoundary';

// Lazy load components for better performance
const LandingPage = React.lazy(() => import('./pages/LandingPage'));
const LoginPage = React.lazy(() => import('./pages/auth/LoginPage'));
const RegisterPage = React.lazy(() => import('./pages/auth/RegisterPage'));
const DashboardPage = React.lazy(() => import('./pages/dashboard/DashboardPage'));
const AssessmentListPage = React.lazy(() => import('./pages/assessments/AssessmentListPage'));
const AssessmentCreatePage = React.lazy(() => import('./pages/assessments/AssessmentCreatePage'));
const AssessmentEditPage = React.lazy(() => import('./pages/assessments/AssessmentEditPage'));
const AssessmentTakePage = React.lazy(() => import('./pages/assessments/AssessmentTakePage'));
const AssessmentResultPage = React.lazy(() => import('./pages/assessments/AssessmentResultPage'));
const QuestionBankPage = React.lazy(() => import('./pages/questions/QuestionBankPage'));
const ProfilePage = React.lazy(() => import('./pages/profile/ProfilePage'));
const AnalyticsPage = React.lazy(() => import('./pages/analytics/AnalyticsPage'));
const AdminPage = React.lazy(() => import('./pages/admin/AdminPage'));
const NotFoundPage = React.lazy(() => import('./pages/NotFoundPage'));

// Create Material-UI theme
const theme = createTheme({
  palette: {
    primary: {
      main: '#667eea',
      dark: '#5a67d8',
      light: '#7c8aed',
    },
    secondary: {
      main: '#764ba2',
      dark: '#6b4491',
      light: '#8659b5',
    },
    background: {
      default: '#f8fafc',
      paper: '#ffffff',
    },
    text: {
      primary: '#2d3748',
      secondary: '#4a5568',
    },
  },
  typography: {
    fontFamily: '"Inter", "Roboto", "Helvetica", "Arial", sans-serif',
    h1: {
      fontWeight: 700,
    },
    h2: {
      fontWeight: 600,
    },
    h3: {
      fontWeight: 600,
    },
    h4: {
      fontWeight: 600,
    },
    h5: {
      fontWeight: 500,
    },
    h6: {
      fontWeight: 500,
    },
  },
  shape: {
    borderRadius: 12,
  },
  components: {
    MuiButton: {
      styleOverrides: {
        root: {
          textTransform: 'none',
          fontWeight: 500,
          borderRadius: 8,
        },
      },
    },
    MuiCard: {
      styleOverrides: {
        root: {
          boxShadow: '0 4px 6px -1px rgb(0 0 0 / 0.1), 0 2px 4px -2px rgb(0 0 0 / 0.1)',
          borderRadius: 12,
        },
      },
    },
    MuiTextField: {
      styleOverrides: {
        root: {
          '& .MuiOutlinedInput-root': {
            borderRadius: 8,
          },
        },
      },
    },
  },
});

// Create React Query client
const queryClient = new QueryClient({
  defaultOptions: {
    queries: {
      refetchOnWindowFocus: false,
      retry: 1,
      staleTime: 5 * 60 * 1000, // 5 minutes
    },
  },
});

// Protected Route Component
const ProtectedRoute = ({ children, roles = [] }) => {
  const { isAuthenticated, user, loading } = useAuth();

  if (loading) {
    return <LoadingSpinner />;
  }

  if (!isAuthenticated) {
    return <Navigate to="/login" replace />;
  }

  if (roles.length > 0 && !roles.includes(user?.role)) {
    return <Navigate to="/dashboard" replace />;
  }

  return children;
};

// Public Route Component (redirect to dashboard if logged in)
const PublicRoute = ({ children }) => {
  const { isAuthenticated, loading } = useAuth();

  if (loading) {
    return <LoadingSpinner />;
  }

  if (isAuthenticated) {
    return <Navigate to="/dashboard" replace />;
  }

  return children;
};

// Main App Component
function AppContent() {
  const { checkAuthStatus } = useAuth();

  useEffect(() => {
    checkAuthStatus();
  }, [checkAuthStatus]);

  return (
    <ThemeProvider theme={theme}>
      <CssBaseline />
      <ErrorBoundary>
        <Router>
          <Suspense fallback={<LoadingSpinner />}>
            <Routes>
              {/* Public Routes */}
              <Route 
                path="/" 
                element={
                  <PublicRoute>
                    <LandingPage />
                  </PublicRoute>
                } 
              />
              <Route 
                path="/login" 
                element={
                  <PublicRoute>
                    <LoginPage />
                  </PublicRoute>
                } 
              />
              <Route 
                path="/register" 
                element={
                  <PublicRoute>
                    <RegisterPage />
                  </PublicRoute>
                } 
              />

              {/* Assessment Taking (Public/Private) */}
              <Route 
                path="/take/:accessCode" 
                element={<AssessmentTakePage />} 
              />
              <Route 
                path="/assessment/:id/take" 
                element={<AssessmentTakePage />} 
              />
              <Route 
                path="/result/:resultId" 
                element={<AssessmentResultPage />} 
              />

              {/* Protected Routes */}
              <Route 
                path="/dashboard" 
                element={
                  <ProtectedRoute>
                    <DashboardPage />
                  </ProtectedRoute>
                } 
              />
              <Route 
                path="/profile" 
                element={
                  <ProtectedRoute>
                    <ProfilePage />
                  </ProtectedRoute>
                } 
              />

              {/* Assessment Routes (Instructor/Admin) */}
              <Route 
                path="/assessments" 
                element={
                  <ProtectedRoute roles={['instructor', 'admin']}>
                    <AssessmentListPage />
                  </ProtectedRoute>
                } 
              />
              <Route 
                path="/assessments/create" 
                element={
                  <ProtectedRoute roles={['instructor', 'admin']}>
                    <AssessmentCreatePage />
                  </ProtectedRoute>
                } 
              />
              <Route 
                path="/assessments/:id/edit" 
                element={
                  <ProtectedRoute roles={['instructor', 'admin']}>
                    <AssessmentEditPage />
                  </ProtectedRoute>
                } 
              />

              {/* Question Bank Routes (Instructor/Admin) */}
              <Route 
                path="/questions" 
                element={
                  <ProtectedRoute roles={['instructor', 'admin']}>
                    <QuestionBankPage />
                  </ProtectedRoute>
                } 
              />

              {/* Analytics Routes (Instructor/Admin) */}
              <Route 
                path="/analytics" 
                element={
                  <ProtectedRoute roles={['instructor', 'admin']}>
                    <AnalyticsPage />
                  </ProtectedRoute>
                } 
              />

              {/* Admin Routes */}
              <Route 
                path="/admin" 
                element={
                  <ProtectedRoute roles={['admin']}>
                    <AdminPage />
                  </ProtectedRoute>
                } 
              />

              {/* 404 Page */}
              <Route 
                path="*" 
                element={<NotFoundPage />} 
              />
            </Routes>
          </Suspense>
        </Router>
      </ErrorBoundary>
      
      {/* Toast Notifications */}
      <Toaster
        position="top-right"
        toastOptions={{
          duration: 4000,
          style: {
            background: '#363636',
            color: '#fff',
          },
          success: {
            duration: 3000,
            theme: {
              primary: '#4caf50',
              secondary: '#fff',
            },
          },
          error: {
            duration: 5000,
            theme: {
              primary: '#f44336',
              secondary: '#fff',
            },
          },
        }}
      />
    </ThemeProvider>
  );
}

// Root App Component with Providers
function App() {
  return (
    <Provider store={store}>
      <QueryClientProvider client={queryClient}>
        <AppContent />
      </QueryClientProvider>
    </Provider>
  );
}

export default App;
